mapper.update:
UPDATE chatroom
SET user_1_id = #{user_1_id}, user_2_id = #{user_2_id}, tag = #{tag},
    open_Date = #{open_Date}, seller_info = #{seller_info}
WHERE chatroom_id = #{chatroom_id}

mapper.select:
SELECT chatroom_id, user_1_id, user_2_id, tag, open_Date, seller_info
FROM chatroom
WHERE chatroom_id = #{chatroomId}
